# ********************
import random

def replace_qualifier(full_name, matching_word):
    name_parts = full_name.split(',')
    if len(name_parts) < 3:
        return full_name
    last_name = name_parts[0]
    first_name = name_parts[1]
    middle_name = ','.join(name_parts[2:]).lstrip()
    word_count = 0
    target_word = middle_name.split()[0]
    for i, part in enumerate(matching_word):
        if part == target_word:
            new_word = random.choice(matching_word)
            middle_name = middle_name.replace(target_word, new_word, 1)
            break
    return ','.join([last_name, first_name, middle_name])




